import React, { useState } from 'react';

const TDE = () => (
    <div className="p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-semibold text-red-700 mb-4">Gestion du TDE</h2>
        <p>Interface pour gérer le chiffrement transparent des données.</p>
    </div>
);

const Audit = () => (
    <div className="p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-semibold text-red-700 mb-4">Configuration de l'Audit</h2>
        <p>Interface pour configurer l'audit de la base de données.</p>
    </div>
);

const VPD = () => (
    <div className="p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-semibold text-red-700 mb-4">Gestion du VPD</h2>
        <p>Interface pour gérer les politiques de base de données virtuelle.</p>
    </div>
);

export default function Home() {
    const [activeComponent, setActiveComponent] = useState(null);

    const renderComponent = () => {
        switch (activeComponent) {
            case 'TDE':
                return <TDE />;
            case 'Audit':
                return <Audit />;
            case 'VPD':
                return <VPD />;
            default:
                return null;
        }
    };

    return (
        <div className="container mx-auto py-8">
            <h1 className="text-3xl font-bold mb-8 text-red-700 text-center">
                Gestion de la sécurité de la base de données
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {['TDE', 'Audit', 'VPD'].map((item) => (
                    <button
                        key={item}
                        onClick={() => setActiveComponent(item)}
                        className="hover:shadow-lg transition-shadow border rounded-lg p-6 bg-white text-left focus:outline-none focus:ring-2 focus:ring-red-700"
                    >
                        <h3 className="text-xl font-semibold text-red-700 mb-2">{item}</h3>
                        <p>
                            {item === 'TDE' && 'Gérer le chiffrement transparent des données'}
                            {item === 'Audit' && "Configurer l'audit de la base de données"}
                            {item === 'VPD' && 'Gérer les politiques de base de données virtuelle'}
                        </p>
                    </button>
                ))}
            </div>
            <div className="mt-8">{renderComponent()}</div>
        </div>
    );
}
