import { useState } from 'react';
import Navbar from './Component/layout/Navbar';
import DashboardLayout from './Component/layout/DashboardLayout';
import Login from './pages/Login';
import Home from './pages/Home';
import Dashboard from './pages/Dashboard/dashOne';

function App() {
    const [currentPath, setCurrentPath] = useState('/'); // État pour suivre la route actuelle

    const handleNavigate = (path) => {
        setCurrentPath(path); // Met à jour l'état lorsque la navigation est demandée
        // Ici, vous pourriez utiliser un routeur comme React Router pour un vrai routage
        // Dans cet exemple, on simule le routage en changeant la variable 'currentPath'.
    };
    // Fonction pour afficher le contenu en fonction de la route actuelle
    const renderPage = () => {
        switch (currentPath) {
            case '/':
                return <Home />;
            case '/dashboard':
                return <Dashboard />;
            case '/login':
                return <Login />;
            default:
                return <Home />; // Page par défaut si rien ne correspond
        }
    };
    return (
        <div>
            <Navbar onNavigate={handleNavigate} currentPath={currentPath} />
            <div className="container mx-auto mt-8 p-4">
                {renderPage()}
            </div>
        </div>
    );
}
