import React from 'react';
import { Link, Routes, Route } from 'react-router-dom';

const TDE = () => (
    <div className="p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-semibold text-red-700 mb-4">Gestion du TDE</h2>
        <p>Interface pour gérer le chiffrement transparent des données.</p>
    </div>
);

const Audit = () => (
    <div className="p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-semibold text-red-700 mb-4">Configuration de l'Audit</h2>
        <p>Interface pour configurer l'audit de la base de données.</p>
    </div>
);

const VPD = () => (
    <div className="p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-semibold text-red-700 mb-4">Gestion du VPD</h2>
        <p>Interface pour gérer les politiques de base de données virtuelle.</p>
    </div>
);

export default function Home() {
    return (
        <div className="container mx-auto py-8">
            <h1 className="text-3xl font-bold mb-8 text-red-700 text-center">
                Gestion de la sécurité de la base de données
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <Link to="/tde">
                    <div className="hover:shadow-lg transition-shadow border rounded-lg p-6 bg-white text-left">
                        <h3 className="text-xl font-semibold text-red-700 mb-2">TDE</h3>
                        <p>Gérer le chiffrement transparent des données</p>
                    </div>
                </Link>
                <Link to="/audit">
                    <div className="hover:shadow-lg transition-shadow border rounded-lg p-6 bg-white text-left">
                        <h3 className="text-xl font-semibold text-red-700 mb-2">Audit</h3>
                        <p>Configurer l'audit de la base de données</p>
                    </div>
                </Link>
                <Link to="/vpd">
                    <div className="hover:shadow-lg transition-shadow border rounded-lg p-6 bg-white text-left">
                        <h3 className="text-xl font-semibold text-red-700 mb-2">VPD</h3>
                        <p>Gérer les politiques de base de données virtuelle</p>
                    </div>
                </Link>
            </div>

            {/* Définir les routes pour chaque composant */}
            <Routes>
                <Route path="/tde" element={<TDE />} />
                <Route path="/audit" element={<Audit />} />
                <Route path="/vpd" element={<VPD />} />
            </Routes>
        </div>
    );
}
