import React, { useState, useEffect } from "react";
import RoleList from "../../Component/RolesList";
import RoleModalCreate from "../../Component/RoleModalCreate";
import RoleModalUpdate from "../../Component/RoleModalUpdate";
import { fetchRoles, createRole, updateRole, deleteRole } from "../../Services/RolesService";

const Roles = () => {
    const [roles, setRoles] = useState([]);
    const [loading, setLoading] = useState(false);
    const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
    const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
    const [currentRoleCreate, setCurrentRoleCreate] = useState({ roleName: "", authType: "NOT_IDENTIFIED", password: "" });
    const [currentRoleUpdate, setCurrentRoleUpdate] = useState({ roleName: "", authType: "NOT_IDENTIFIED", password: "" });

    // Fetch roles on component mount
    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const data = await fetchRoles();
                setRoles(data);
            } catch (err) {
                console.error("Error fetching roles:", err);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    // Modal handlers
    const handleCreateModalOpen = () => {
        setIsCreateModalOpen(true);
        setCurrentRoleCreate({ roleName: "", authType: "NOT_IDENTIFIED", password: "" });
    };

    const handleCreateModalClose = () => {
        setIsCreateModalOpen(false);
        setCurrentRoleCreate({ roleName: "", authType: "NOT_IDENTIFIED", password: "" });
    };

    const handleUpdateModalOpen = (role) => {
        setIsUpdateModalOpen(true);
        setCurrentRoleUpdate({
            roleName: role.roleName,
            authType: role.authType,
            password: role.password,
        });
    };

    const handleUpdateModalClose = () => {
        setIsUpdateModalOpen(false);
        setCurrentRoleUpdate({ roleName: "", authType: "NOT_IDENTIFIED", password: "" });
    };

    // Input handlers
    const handleCreateInputChange = (e) => {
        const { name, value } = e.target;
        setCurrentRoleCreate({ ...currentRoleCreate, [name]: value });
    };

    const handleUpdateInputChange = (e) => {
        const { name, value } = e.target;
        setCurrentRoleUpdate({ ...currentRoleUpdate, [name]: value });
    };

    // Submit handlers
    const handleCreateSubmit = async () => {
        try {
            await createRole(currentRoleCreate);
            const updatedRoles = await fetchRoles();
            setRoles(updatedRoles);
            handleCreateModalClose();
        } catch (err) {
            console.error("Error creating role:", err);
        }
    };

    const handleUpdateSubmit = async () => {
        try {
            await updateRole(currentRoleUpdate.roleName, currentRoleUpdate);
            const updatedRoles = await fetchRoles();
            setRoles(updatedRoles);
            handleUpdateModalClose();
        } catch (err) {
            console.error("Error updating role:", err);
        }
    };

    const handleDelete = async (roleName) => {
        try {
            await deleteRole(roleName);
            alert("Rôle supprimé avec succès !");
            const updatedRoles = await fetchRoles();
            setRoles(updatedRoles);
        } catch (error) {
            console.error("Erreur lors de la suppression du rôle :", error);
            alert("Erreur lors de la suppression du rôle.");
        }
    };

    return (
        <div>
            <button onClick={handleCreateModalOpen} className="text-2xl font-semibold mb-4 text-green-600">
                Ajouter un rôle
            </button>
            <RoleList
                roles={roles}
                loading={loading}
                onEdit={handleUpdateModalOpen}
                onDelete={handleDelete}
            />
            <RoleModalCreate
                isOpen={isCreateModalOpen}
                role={currentRoleCreate}
                onClose={handleCreateModalClose}
                onChange={handleCreateInputChange}
                onSubmit={handleCreateSubmit}
            />
            <RoleModalUpdate
                isOpen={isUpdateModalOpen}
                role={currentRoleUpdate}
                onClose={handleUpdateModalClose}
                onChange={handleUpdateInputChange}
                onSubmit={handleUpdateSubmit}
            />
        </div>
    );
};

export default Roles;
