import React from 'react';
import { Modal, Button, Select } from '@nextui-org/react';

const UserModalGrantRole = ({ isOpen, users, selectedUserId, onChange, onClose, onSubmit }) => {
    return (
        <Modal closeButton open={isOpen} onClose={onClose}>
            <Modal.Header>
                <h3>Attribuer un rôle à un utilisateur</h3>
            </Modal.Header>
            <Modal.Body>
                <Select
                    label="Sélectionnez un utilisateur"
                    value={selectedUserId}
                    onChange={onChange}
                    fullWidth
                >
                    {users.map((user) => (
                        <Select.Option key={user.id} value={user.id}>
                            {user.username}
                        </Select.Option>
                    ))}
                </Select>
            </Modal.Body>
            <Modal.Footer>
                <Button auto flat color="error" onClick={onClose}>
                    Annuler
                </Button>
                <Button auto onClick={onSubmit}>
                    Attribuer le rôle
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default UserModalGrantRole;
