import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiUser, FiClock, FiEdit2, FiTrash2, FiPlus, FiShield } from 'react-icons/fi';
import * as http from "node:http";

const Users = () => {
    const [users, setUsers] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [newUser, setNewUser] = useState({
        username: '',
        password: '',
    });
    const [editUser, setEditUser] = useState({
        id: null,
        username: '',
        password: '',
    });
    const [alertMessage, setAlertMessage] = useState('');
    const [isError, setIsError] = useState(false);
    const [loading, setLoading] = useState(false);


    const fetchUsers = async () => {
        try {
            setLoading(true);
            const response = await axios.get('http://localhost:8080/users');
            setUsers(response.data);
            setLoading(false);
        } catch (err) {
            console.error('erreur lors de la récupération des utilisateurs', err);
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);


    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setNewUser({ username: '', password: '' }); // Réinitialiser le formulaire
        setAlertMessage('');
        setIsError(false);
    };
    const openEditModal = (user) => {
        setEditUser({ id: user.id, username: user.username, password: "" });
        setIsEditModalOpen(true);
    };

    const closeEditModal = () => {
        setIsEditModalOpen(false);
        setEditUser({ id: null, username: '', password: '' });
        setAlertMessage('');
        setIsError(false);
    };


    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewUser({ ...newUser, [name]: value });
    };

    const handleEditInputChange = (e) => {
        const { name, value } = e.target;
        setEditUser({ ...editUser, [name]: value });
    };


    const createUser = async () => {
        try {
            const response = await axios.post("http://localhost:8080/CreateUser", newUser);
            fetchUsers();// On refait l'appel d'api pour que l'ajout se répercute sur l'UI
            setAlertMessage('User created successfully!');
            setIsError(false);
            closeModal();

        } catch (err) {
            console.error('Error creating user:', err);
            setAlertMessage(err.response?.data?.message || "Internal Server Error"); //extracting error message from the server response
            setIsError(true);
        }
    };
    const updateUser = async () => {
        try {
            await axios.put('http://localhost:8080/users/${editUser.id}', editUser);
            fetchUsers();
            setAlertMessage('User updated successfully!');
            setIsError(false);
            closeEditModal();
        } catch (err) {
            console.error('Error updating user:', err);
            setAlertMessage(err.response?.data?.message || "Failed to update user.");
            setIsError(true);
        }
    };

    const grantRole = async (userId) => {
        try{
            await axios.post('http://localhost:8080/grantRole/${userId}');
            setAlertMessage("role user has been granted successfully");
            setIsError(false);
        }catch(err){
            console.error('Error grant user:', err);
            setAlertMessage(err.response?.data?.message || "Failed to grant role.");
            setIsError(true);
        }

    };



    useEffect(() => {
        if (alertMessage){
            alert(alertMessage)
        }
    }, [alertMessage]);


    return (
        <div className="p-6">

            <div className="flex justify-between items-center mb-4">
                <h2 className="text-3xl font-bold  text-gray-900">Gestion des Utilisateurs</h2>
                <button onClick={openModal} className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded flex items-center" >
                    <FiPlus className='mr-2' /> Ajouter un utilisateur
                </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {loading ? (  <div className="flex justify-center items-center  h-48  bg-gray-200 rounded-lg ">
                        Chargement des utilisateurs ...
                    </div>)
                    :(users.length > 0 ? (
                        users.map(user => (
                            <div key={user.id} className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-2xl transition-shadow duration-300 ease-in-out">
                                <div className="p-6 flex items-center justify-between">
                                    <div className="w-2/3 flex flex-col gap-1">
                                        <h3 className="text-xl font-semibold text-gray-800 flex items-center">
                                            <FiUser className="mr-2 text-blue-500" /> {user.username}
                                        </h3>

                                        <p className="flex items-center text-gray-600 text-sm mt-2"><FiClock className="mr-1 text-gray-400" />  <time className="relative top-[1px]">{user.dateCreation}</time> </p>
                                    </div>
                                    <div className=" flex flex-col w-[55px] gap-3 justify-center h-full  ">
                                        <button title='modifier' onClick={() => openEditModal(user)} className=" flex w-10 h-10 p-[10px] hover:bg-blue-50  text-blue-500 hover:text-blue-700 rounded-full ">
                                            <FiEdit2 /> </button>
                                        <button title='supprimer' className="flex  w-10 h-10 p-[10px] hover:bg-red-50   text-red-500 hover:text-red-700 rounded-full">  <FiTrash2 /> </button>
                                        <button  title='Ajouter un role' onClick={() => grantRole(user.id)} className="flex  w-10 h-10 p-[10px] hover:bg-green-50   text-green-500 hover:text-green-700 rounded-full">  <FiShield /> </button>
                                    </div>
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="flex justify-center items-center  h-48  bg-gray-200 rounded-lg ">
                            Aucun utilisateur trouvé
                        </div>
                    ))}
            </div>

            {/* MODAL FOR CREATE USER */}
            {isModalOpen && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
                        <h2 className="text-2xl font-semibold mb-4">Ajouter un nouvel utilisateur</h2>

                        <div className="mb-4">
                            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="username">Nom d'utilisateur</label>
                            <input
                                type="text"
                                name="username"
                                value={newUser.username}
                                onChange={handleInputChange}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                required
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">Mot de passe</label>
                            <input
                                type="password"
                                name="password"
                                value={newUser.password}
                                onChange={handleInputChange}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                required
                            />
                        </div>
                        <div className="flex justify-end">
                            <button onClick={closeModal} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2">
                                Annuler
                            </button>
                            <button onClick={createUser} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                                Créer l'utilisateur
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* MODAL FOR EDIT USER */}
            {isEditModalOpen && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
                        <h2 className="text-2xl font-semibold mb-4">Modifier utilisateur</h2>

                        <div className="mb-4">
                            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="username">Nom d'utilisateur</label>
                            <input
                                type="text"
                                name="username"
                                value={editUser.username}
                                onChange={handleEditInputChange}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                required
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">Mot de passe</label>
                            <input
                                type="password"
                                name="password"
                                value={editUser.password}
                                onChange={handleEditInputChange}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                        </div>
                        <div className="flex justify-end">
                            <button onClick={closeEditModal} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2">
                                Annuler
                            </button>
                            <button onClick={updateUser} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                                Modifier l'utilisateur
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};
