import {useNavigate} from "react-router-dom";
import {useEffect} from "react";
function Home(){

    const navigate = useNavigate(); // utilisation de useNavigate

    useEffect(() => {
        const token = localStorage.getItem('jwtToken'); // Récupérer le token depuis le localStorage

        if (!token) {
            // Si le token n'existe pas, rediriger vers la page de login
            navigate('/login');
        }
        // Pas besoin de faire la redirection vers home si le token existe, car c'est là ou on se trouve déjà.

    }, [navigate]); // Ajouter navigate comme dépendance pour l'effet
    return (
        <div>
                <h1>
                    welcom to your ORACLE DATABASE
                </h1>
        </div>
    );
}



export default Home;
