import React from 'react';
import { FiUser, FiClock, FiEdit2, FiTrash2, FiShield } from 'react-icons/fi';

const UserList = ({ users, loading, onEdit, onGrantRole }) => {
    if (loading) {
        return <div>Chargement des utilisateurs...</div>;
    }

    if (!users.length) {
        return <div>Aucun utilisateur trouv√©.</div>;
    }

    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {users.map((user) => (
                <div key={user.id} className="bg-white rounded-lg shadow-lg overflow-hidden">
                    <div className="p-6 flex items-center justify-between">
                        <div>
                            <h3 className="text-xl font-semibold text-gray-800 flex items-center">
                                <FiUser className="mr-2 text-blue-500" />
                                {user.username}
                            </h3>
                            <p className="text-sm text-gray-600">
                                <FiClock className="mr-1" />
                                {user.dateCreation}
                            </p>
                        </div>
                        <div>
                            <button onClick={() => onEdit(user)}>
                                <FiEdit2 />
                            </button>
                            <button onClick={() => onGrantRole(user.id)}>
                                <FiShield />
                            </button>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default UserList;
