import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Vous devrez l'installer avec : npm install axios

const Users = () => {
    const [users, setUsers] = useState([]);

    // Exemple de fonction qui récuperer les données de l'api, vous devrer le modifier avec l'url correcte
    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/users');
            setUsers(response.data)
        } catch (err) {
            console.error('erreur lors de la récupération des utilisateurs', err);
        }
    };

    useEffect(() => {
        fetchUsers(); // appeler l'api à l'initialisation.
    }, []) // dependency est un tableau vide, donc on s'execute 1 seul fois


    // Exemple pour affihcer une liste avec tous les users

    return (
        <div>
            <h2 className="text-2xl font-bold mb-4">Gestion des Utilisateurs</h2>
            {users.length > 0 ?  ( <div className="bg-white shadow-md rounded-md p-4 overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-gray-50">
                        <tr>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Username</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date de création</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
                        </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                        {users.map((user) => (
                            <tr key={user.id}>
                                <td className="px-6 py-4 whitespace-nowrap">{user.username}</td>
                                <td className="px-6 py-4 whitespace-nowrap">{user.email}</td>
                                <td className="px-6 py-4 whitespace-nowrap">{user.createdAt}</td>
                                <td className="px-6 py-4 whitespace-nowrap">
                                    <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mr-2"  >Supprimer</button>
                                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Modifier</button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            ) :( <p>Chargement des users</p>)}
        </div>
    );
};

export default Users;
