import React, { useState, useEffect } from 'react';
import UserList from '../../Component/UsersList';
import UserModalCreate from '../../Component/UserModelCreate';
import UserModalUpdate from '../../Component/UserModelUpdate';
import { fetchUsers, createUser, updateUser, grantRole } from '../../Services/UsersService';

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentUser, setCurrentUser] = useState({ username: '', password: '' });
    const [isEdit, setIsEdit] = useState(false);
    const [currentUserUpdate, setCurrentUserUpdate] = useState({ username: '', enable:'',defaulttablespace:'' });

    useEffect(() => {
        setLoading(true);
        fetchUsers()
            .then((data) => {
                setUsers(data);
                setLoading(false);
            })
            .catch(() => setLoading(false));
    }, []);

    const handleModalOpen = (user = { username: '', password: '' }) => {
        setCurrentUser(user);
        setIsEdit(!!user.id);
        setIsModalOpen(true);
    };

    const handleModalClose = () => {
        setIsModalOpen(false);
        setCurrentUser({ username: '', password: '' });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCurrentUser({ ...currentUser, [name]: value });
    };

    const handleSubmit = async () => {
        try {
            if (isEdit) {
                await updateUser(currentUser.id, currentUser);
            } else {
                await createUser(currentUser);
            }
            const updatedUsers = await fetchUsers();
            setUsers(updatedUsers);
            handleModalClose();
        } catch (err) {
            console.error(err);
        }
    };

    const handleGrantRole = async (userId) => {
        try {
            await grantRole(userId);
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div>
            <button onClick={() => handleModalOpen()} className="text-2xl font-semibold mb-4 text-green-600">Ajouter un utilisateur</button>
            <UserList
                users={users}
                loading={loading}
                onEdit={handleModalOpen}
                onGrantRole={handleGrantRole}
            />
            <UserModalCreate
                isOpen={isModalOpen}
                user={currentUser}
                onClose={handleModalClose}
                onChange={handleInputChange}
                onSubmit={handleSubmit}
            />
            <UserModalUpdate
                isOpen={isModalOpen}
                userUpdate={currentUser}
                onClose={handleModalClose}
                onChange={handleInputChange}
                onSubmit={handleSubmit}
            />
        </div>
    );
};

export default Users;
