import { useState } from 'react';
import Navbar from './Component/layout/Navbar';
import DashboardLayout from './Component/layout/DashboardLayout';
import Login from './pages/Login';
import Home from './pages/Home';
import Dashboard from './pages/Dashboard/dashOne';

function App() {
    // État pour gérer le chemin actuel et l'authentification
    const [currentPath, setCurrentPath] = useState('/');
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Gestionnaire de navigation
    const handleNavigate = (path) => {
        setCurrentPath(path);
    };

    // Gestionnaire de connexion
    const handleLogin = (formData) => {
        // Ici vous pouvez ajouter votre logique d'authentification
        console.log('Tentative de connexion avec:', formData);
        setIsAuthenticated(true);
        handleNavigate('/dashboard');
    };

    // Gestionnaire de déconnexion
    const handleLogout = () => {
        setIsAuthenticated(false);
        handleNavigate('/');
    };

    // Rendu du contenu principal
    const renderContent = () => {
        switch (currentPath) {
            case '/login':
                return <Login onLogin={handleLogin} />;

            case '/dashboard':
                if (isAuthenticated) {
                    handleNavigate('/login');
                    return null;
                }
                return (
                    <DashboardLayout
                        onNavigate={handleNavigate}
                        currentPath={currentPath}
                        onLogout={handleLogout}
                    >
                        <Dashboard />
                    </DashboardLayout>
                );

            case '/dashboard/stats':
                if (!isAuthenticated) {
                    handleNavigate('/login');
                    return null;
                }
                return (
                    <DashboardLayout
                        onNavigate={handleNavigate}
                        currentPath={currentPath}
                        onLogout={handleLogout}
                    >
                        <div className="p-4">
                            <h2 className="text-2xl font-bold">Statistiques</h2>
                            {/* Contenu de la page statistiques */}
                        </div>
                    </DashboardLayout>
                );

            case '/dashboard/settings':
                if (!isAuthenticated) {
                    handleNavigate('/login');
                    return null;
                }
                return (
                    <DashboardLayout
                        onNavigate={handleNavigate}
                        currentPath={currentPath}
                        onLogout={handleLogout}
                    >
                        <div className="p-4">
                            <h2 className="text-2xl font-bold">Paramètres</h2>
                            {/* Contenu de la page paramètres */}
                        </div>
                    </DashboardLayout>
                );

            default:
                return <Home />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {currentPath !== '/login' && (
                <Navbar
                    onNavigate={handleNavigate}
                    currentPath={currentPath}
                    isAuthenticated={isAuthenticated}
                    onLogout={handleLogout}
                />
            )}

            <main className={currentPath === '/login' ? '' : 'container mx-auto px-4'}>
                {renderContent()}
            </main>
        </div>
    );
}

export default App;
