import React, { useState } from 'react';
import { FiShield, FiTrash } from 'react-icons/fi';
import ConfirmationPopup from './ConfirmationPopup'; // Importer le composant de pop-up

export const RolesList = ({ roles, loading, onGrant, onDelete }) => {
    const [showPopup, setShowPopup] = useState(false); // État pour afficher/masquer la pop-up
    const [roleToDelete, setRoleToDelete] = useState(null); // État pour stocker le rôle à supprimer

    // Fonction pour afficher la pop-up de confirmation
    const handleDeleteClick = (roleId) => {
        setRoleToDelete(roleId); // Stocker l'ID du rôle à supprimer
        setShowPopup(true); // Afficher la pop-up
    };

    // Fonction pour confirmer la suppression
    const handleConfirmDelete = () => {
        onDelete(roleToDelete); // Appeler la fonction onDelete avec l'ID du rôle
        setShowPopup(false); // Masquer la pop-up
        setRoleToDelete(null); // Réinitialiser le rôle à supprimer
    };

    // Fonction pour annuler la suppression
    const handleCancelDelete = () => {
        setShowPopup(false); // Masquer la pop-up
        setRoleToDelete(null); // Réinitialiser le rôle à supprimer
    };

    if (loading) {
        return <div className="flex justify-center items-center h-48 bg-gray-200 rounded-lg">Chargement des rôles...</div>;
    }

    if (!roles.length) {
        return <div className="flex justify-center items-center h-48 bg-gray-200 rounded-lg">Aucun rôle trouvé.</div>;
    }

    return (
        <div>
            <h2 className="text-2xl font-bold mb-4 text-red-700">Gestion des Rôles</h2>
            {roles.length > 0 ? (
                <div className="bg-white p-4 shadow-md rounded-md">
                    <ul className="divide-y divide-gray-200">
                        {roles.map((role) => (
                            <li key={role.id} className="flex justify-between py-2">
                                <span className="font-semibold"> {role.name || role.roleName}</span>
                                <div className="flex gap-4">
                                    <button
                                        className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
                                        onClick={() => onGrant(role.id)}
                                    >
                                        <FiShield />
                                    </button>
                                    <button
                                        className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                                        onClick={() => handleDeleteClick(role.id)} // Afficher la pop-up de confirmation
                                    >
                                        <FiTrash />
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            ) : (
                <p>chargement des rôles...</p>
            )}

            {/* Afficher la pop-up de confirmation si showPopup est true */}
            {showPopup && (
                <ConfirmationPopup
                    message="Êtes-vous sûr de vouloir supprimer ce rôle ?"
                    onConfirm={handleConfirmDelete}
                    onCancel={handleCancelDelete}
                />
            )}
        </div>
    );
};

export default RolesList;
