import { useState } from 'react';

const DashboardLayout = ({ children, onNavigate, currentPath = '/dashboard' }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    return (
        <div className="h-screen flex overflow-hidden bg-gray-100">
            {/* Sidebar */}
            <div className={`${
                isSidebarOpen ? 'w-64' : 'w-20'
            } transition-width duration-300 ease-in-out bg-white shadow-md`}>
                <div className="h-full flex flex-col">
                    <div className="flex items-center justify-between h-16 px-4">
            <span className="text-lg font-bold text-gray-900">
              Menu
            </span>
                        <button
                            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                            className="text-gray-500 hover:text-gray-900"
                        >
                            {isSidebarOpen ? '←' : '→'}
                        </button>
                    </div>
                    <nav className="flex-1 space-y-1 px-2 py-4">
                        <button
                            onClick={() => onNavigate('/dashboard')}
                            className={`flex items-center w-full px-2 py-2 text-left rounded-md ${
                                currentPath === '/dashboard'
                                    ? 'bg-blue-50 text-blue-700'
                                    : 'text-gray-900 hover:bg-gray-100'
                            }`}
                        >
                            <span>Tableau de bord</span>
                        </button>
                        <button
                            onClick={() => onNavigate('/dashboard/stats')}
                            className={`flex items-center w-full px-2 py-2 text-left rounded-md ${
                                currentPath === '/dashboard/stats'
                                    ? 'bg-blue-50 text-blue-700'
                                    : 'text-gray-900 hover:bg-gray-100'
                            }`}
                        >
                            <span>Statistiques</span>
                        </button>
                        <button
                            onClick={() => onNavigate('/dashboard/settings')}
                            className={`flex items-center w-full px-2 py-2 text-left rounded-md ${
                                currentPath === '/dashboard/settings'
                                    ? 'bg-blue-50 text-blue-700'
                                    : 'text-gray-900 hover:bg-gray-100'
                            }`}
                        >
                            <span>Paramètres</span>
                        </button>
                    </nav>
                </div>
            </div>

            {/* Contenu principal */}
            <div className="flex-1 overflow-auto">
                <main className="flex-1 relative z-0 overflow-y-auto focus:outline-none">
                    <div className="py-6">
                        <div className="max-w-7xl mx-auto px-4 sm:px-6 md:px-8">
                            {children}
                        </div>
                    </div>
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
