import React, { useState, useEffect } from "react";
import { fetchRoles } from "../Services/UsersService";

const GrantRoleModal = ({ isOpen, onClose, userId, onGrantRole }) => {
    const [roles, setRoles] = useState([]);
    const [selectedRole, setSelectedRole] = useState("");
    const [loading, setLoading] = useState(false);

    // Fetch roles on component mount
    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const data = await fetchRoles();
                setRoles(data);
            } catch (err) {
                console.error("Error fetching roles:", err);
            } finally {
                setLoading(false);
            }
        };

        if (isOpen) {
            fetchData();
        }
    }, [isOpen]);

    // Handle role selection
    const handleRoleChange = (e) => {
        setSelectedRole(e.target.value);
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await onGrantRole(userId, selectedRole);
            alert("Rôle accordé avec succès !");
            onClose(); // Close the modal after successful grant
        } catch (err) {
            console.error("Error granting role:", err);
            alert("Erreur lors de l'octroi du rôle.");
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded-lg shadow-lg w-1/3">
                <h2 className="text-xl font-semibold mb-4">Accorder un rôle à l'utilisateur</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Sélectionner un rôle</label>
                        <select
                            value={selectedRole}
                            onChange={handleRoleChange}
                            className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                            required
                        >
                            <option value="" disabled>Sélectionnez un rôle</option>
                            {roles.map((role) => (
                                <option key={role.id} value={role.name}>
                                    {role.name}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="flex justify-end">
                        <button
                            type="button"
                            onClick={onClose}
                            className="mr-2 px-4 py-2 bg-gray-500 text-white rounded-md"
                        >
                            Annuler
                        </button>
                        <button
                            type="submit"
                            className="px-4 py-2 bg-green-600 text-white rounded-md"
                        >
                            Accorder
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default GrantRoleModal;
